import { Box, Chip, Typography } from "@mui/material";
import axios from "axios"
import { useEffect, useState } from "react"
import PokemonCard from "./card";

const PokeCard = ({pokemons}) => {

    const [currentPokemon, setCurrentPokemon] = useState()

    useEffect(() => {
        axios
            .get(pokemons[0].url)
            .then(res => setCurrentPokemon(res.data))
    }, [pokemons]);

    const handleClick = (e) => {
        setCurrentPokemon(e.target.value)
    }

    return (
        <Box sx={{
            width: 980, 
            height: 500,
            display: 'flex',
            gap: '12px'
        }}>
            <Box sx={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'start',
                flexWrap: 'wrap',
                gap: 1.5,
                flex: '1 1 484px',
            }}>
                {
                    pokemons.map(pokemon => {
                        let name = pokemon.name;
                        return (
                            <Chip 
                                key={name}
                                label={name}
                                sx={{
                                    bgcolor: '#1986EC',
                                    color: '#ffffff',
                                    fontSize: 20,
                                    py: '30px',
                                    px: 1,
                                    borderRadius: 11,
                                }}
                                onClick={handleClick}
                            />
                        )
                    })
                }
            </Box>
            <Box sx={{
                flex: '1 1 484px',
                bgcolor: '#000000',
                p: 5.5,
                boxSizing: 'border-box',
            }}>
                {
                    currentPokemon && 
                    <PokemonCard pokemon={currentPokemon}/>
                }
            </Box>
        </Box>
    )
} 

export default PokeCard
