import { Box, Chip, Typography } from "@mui/material";
import axios from "axios"
import { useEffect, useState } from "react"

const PokeCard = ({data}) => {

    const [currentPokemon, setCurrentPokemon] = useState()

    useEffect(() => {
        axios
            .get(data[0].url)
            .then(res => setCurrentPokemon(res.data))
    }, [data]);

    return (
        <Box sx={{
            width: 980, 
            height: 500,
            display: 'flex',
            gap: '12px'
        }}>
            <Box sx={{
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'start',
                flexWrap: 'wrap',
                gap: 1.5,
                flex: '1 1 484px',
            }}>
                {
                    data.map(pokemon => {
                        let name = pokemon.name;
                        return (
                            <Chip 
                                key={name}
                                label={name}
                                sx={{
                                    bgcolor: '#1986EC',
                                    color: '#ffffff',
                                    fontSize: 20,
                                }}
                            />
                        )
                    })
                }
            </Box>
            <Box sx={{
                flex: '1 1 484px',
                bgcolor: '#000000',
                p: 5.5,
                boxSizing: 'border-box',
            }}>
                {
                    currentPokemon && 
                    <Typography component='h1' sx={{
                        color: '#ffffff',
                        fontSize: 48,
                    }}>
                        {currentPokemon.name[0].toUpperCase() + currentPokemon.name.slice(1)}
                    </Typography>
                }
            </Box>
        </Box>
    )
} 

export default PokeCard
